<?php/** * Created by PhpStorm. * User: pantian * Date: 2014/12/26 * Time: 22:51 */namespace AtServer\DB;/** * 数据模型抽象类 * Class DBModel */abstract class IModelInterface{    /**     * @var PtPDO     */    public $PDO = null;    public $_table = null;    /**     * 表的全名     * @var null     */    public $fullTableName = null;    /**     * 设置字段默认值     * 如： ['time'=>time()]     * @var array     */    public $_DefaultFieldValue = array();    /**     * 表单字段验证     * *array(     * array('规则类型','验证字段(array())','验证规则'),     * )     *      规则类型：     *              Required 必填     *              Email 邮件     *              Mobile 手机号码     *              MaxLength 最大长度     *              MinLength 最小长度     *              Length 长度     *              Number 纯数字     *              Max 最大值     *              Min 最小值     *              String 字符串     *              unique 唯一的     *      验证规则：     *              可以是字符串，也可以是数组     *      验证字段：     *              array("字段1",""字段2,""字段3);     * @var array     */    public $_Rule = array();    /**     * 错误信息     * @var string     */    protected $Error_Message='';    /**     * 字段描述     * ['name'=>'用户名']     * @var array     */    public $_FieldDescription = array();    /**     * 主键名     * @var string     */    public $PK = '';    /**     * 主键值     * @var string     */    public $PKV = '';    /**     * 验证规则     * @var array     */    // $_Rule = array();    /**     * 是否开启表单字段验证，默认开启     * @var bool     */    public $DoVerifyRule = TRUE;    /**     * 字段含义（定义）描述     * @var array     */    // public $FieldDescription = array();    /**     * 当前控制器的引用     * @var null     */    public $action = null;    abstract function add($data = array());    abstract function save();    abstract function delete($where);    abstract function getByPK($id);    abstract function setInc($field, $step = 1);}